export async function getServerSideProps({
  query
}) {
  const {
    publicRuntimeConfig
  } = getConfig();

  const deneme = () => {
    if (query.kategori) {
      if (query.page) {
        const resArticles = fetch(`${publicRuntimeConfig.apiEndpoint}kategorifilter/?kategori=${query.kategori}&page=${query.page}`);
        return resArticles;
      } else {
        const resArticles = fetch(`${publicRuntimeConfig.apiEndpoint}kategorifilter/?kategori=${query.kategori}`);
        return resArticles;
      }
    } else {
      if (query.page) {
        const resArticles = fetch(`${publicRuntimeConfig.apiEndpoint}kategorifilter/?page=${query.page}`);
        return resArticles;
      } else {
        const resArticles = fetch(`${publicRuntimeConfig.apiEndpoint}kategorifilter/`);
        return resArticles;
      }
    }
  };

  const [resArticles, resPopular] = await Promise.all([deneme(), fetch(`${publicRuntimeConfig.apiEndpoint}kategorifilter/`)]);
  const [articles, popularArticles] = await Promise.all([resArticles.json(), resPopular.json()]);
  return {
    props: {
      articles,
      popularArticles,
      page: parseInt(query.page) ? query.page : 1,
      kategori: query.kategori && query.kategori
    }
  };
}
export default hukukiyayinlar;
